data.introspection_response:
schema {
  query: Query
  mutation: Mutation
}
type AddToContactResponse {
  "detail"
  detail: String!
}
type CreateChatResponse {
  "id"
  id: String!
}
type GetMyContactResponse {
  "id"
  id: String!
  ""
  isMyContact: Boolean!
  "name"
  name: String!
}
type Mutation {
  addToContacts(contact: AddToContactInput!): AddToContactResponse!
  createChat(chat_data: CreateChatInput!): CreateChatResponse!
  register(input: RegisterInput!): RegisterResponse!
  sendMessage(message_data: SendMessageInput!): SendMessageResponse!
}
type Query {
  getContact(search: GetMyContactInput!): [GetMyContactResponse!]!
}
type RegisterResponse {
  "description"
  id: String!
}
type SendMessageResponse {
  "id"
  id: String!
}
input AddToContactInput {
  "user_id"
  user_id: String!
}
input CreateChatInput {
  "participant_id"
  participant_id: String!
  "user_id"
  user_id: String!
}
input GetMyContactInput {
  "is_success"
  search_field: String
}
input RegisterInput {
  "Avatar URL"
  avatar_url: String
  "User email (login)"
  email: String!
  "User name"
  name: String!
  "User password"
  password: String!
  "User patronymic"
  patronymic: String
  "User surname"
  surname: String!
}
input SendMessageInput {
  ""
  adds: [String!]
  "id"
  chat_id: String
  ""
  message: String!
  "participant_id"
  participant_id: String!
}
"Indicates exactly one field must be supplied and this field must not be `null`."
directive @oneOf on INPUT_OBJECT
"Exposes a URL that specifies the behavior of this scalar."
directive @specifiedBy(
    "The URL that specifies the behavior of this scalar."
    url: String!
  ) on SCALAR
